function easyDifficultyGameplay() {
    game.onUpdateInterval(800, function () {
        random = randint(0, 1)
        target = sprites.create(img`
        c c c c c . . . . . . . . . . . 
        c 6 7 7 7 c c . . . . . . . . . 
        . c c 7 7 7 c c . . . . . . . . 
        . . . c 7 7 6 c . . . . . . . . 
        . . . c 6 6 6 c . . . . . . . . 
        . . c c 6 6 6 c . . . . . . . . 
        . c c 6 6 6 c c c c c c c . . . 
        . c 6 6 6 c c 6 7 7 7 7 6 c . . 
        c c 6 6 6 c 7 7 7 7 7 7 7 7 c . 
        c 6 6 6 c 6 7 7 7 7 7 7 7 7 6 c 
        c 6 6 6 c 7 7 7 c 6 6 6 6 c 7 c 
        c 6 6 6 f 7 7 7 c c 6 6 c c 7 f 
        c 6 6 6 f 7 7 7 6 f 6 6 f 6 7 f 
        . c 6 6 f 6 7 7 7 7 7 7 7 7 f . 
        . c c 6 6 f 6 7 c 1 f f c 1 c . 
        . . . c c c c c c c c c c c c . 
        `, SpriteKind.Enemy)
        target.setFlag(SpriteFlag.AutoDestroy, true)
        if (random == 0) {
            target.setPosition(0, randint(0, 160))
        } else {
            target.setPosition(160, randint(0, 160))
        }
        target.setVelocity(randint(-50, 50), randint(-50, 50))
        if (target.x > 160 || target.x < 0) {
            target.destroy();
            console.log("Target X-Value destroyed")
        }
        if (target.y > 120 || target.y < 0) {
            target.destroy();
            console.log("Target Y-Value destroyed")
        }
    })
    console.log("800")
}
